import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDate, setEnteredDate] = useState("");
    const [addExpenseOption, setAddExpenseOption] = useState(true);

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };

    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    };

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    };

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate),
        };

        props.onSaveExpenseData(expenseData);
        setEnteredTitle("");
        setEnteredAmount("");
        setEnteredDate("");
    };

    const cancelClickedHandler = () => {
    };

    const addExpense = () => {
        return (
            <form onSubmit={submitHandler}>
                <div className="new-expense__controls">
                    <div className="new-expense__control">
                        <label>Title</label>
                        <input
                            placeholder="Expense"
                            required
                            type="text"
                            value={enteredTitle}
                            onChange={titleChangeHandler}
                        />
                    </div>
                    <div className="new-expense__control">
                        <label>Amount</label>
                        <input
                            placeholder="DKK"
                            required
                            type="number"
                            min="0.01"
                            step="0.01"
                            value={enteredAmount}
                            onChange={amountChangeHandler}
                        />
                    </div>
                    <div className="new-expense__control">
                        <label>Date</label>
                        <input
                            required
                            type="date"
                            min="2019-01-01"
                            max="2023-12-31"
                            value={enteredDate}
                            onChange={dateChangeHandler}
                        />
                    </div>
                </div>
                <div className="new-expense__actions">
                    <button onClick={cancelClickedHandler}>Cancel</button>
                    <button type="submit">Add Expense</button>
                </div>
            </form>
        );
    };

    const defaultView = () => {
        return (
            <button
                className="default-button"
                type="submit"
                onClick={addExpenseOptionHandler}
            >
                Add Expense
            </button>
        );
    };

    const addExpenseToggleHandler = () => {
      addExpenseOption === false ? setAddExpenseOption(true) : setAddExpenseOption(false) 
    }
    const addExpenseOptionHandler = () => {
        setAddExpenseOption(true);
    };

    return addExpenseOption === false ? defaultView() : addExpense();
};

export default ExpenseForm;
